const form = document.getElementById('myform');
const text = document.getElementById('message');
const container = document.querySelector('.mycontainer')
const event = new EventEmitter();
event.on('recieve',data=>{
    append(`Name:${message}`)
})
event.on('send',()=>{
    event.emit('recieve',{Name,message:message});
})
form.addEventListener('submit',(e)=>{
    e.preventDefault();
    message = text.value;
    append(`Name:${message}`)
    message = '';
});
event.on('append',()=>{
    const rowelement = document.createElement('div');
    rowelement.innertext = message;
    rowelement.classListAdd(message)
    mycontainer.append('rowelement')
})